import { useState } from "react";


function BookCreate({onCreate}){
    const [title,setTitle] = useState('');

    const handleChange = (event) =>{
       
        setTitle(event.target.value);   
       
    };
    const handleSubmit = (event)=>{
        event.preventDefault();
        onCreate(title);
        setTitle('');
    }


    return <div>

        <h3>Add a book</h3>
      
        
        <form className="message is-primary" onSubmit={handleSubmit}>
            <label className="message-header">Title</label>
            <input placeholder="enter books here" 
            className=" input is-primary m-1" value={title}
             onChange={handleChange}/>
            <button className="button is-success is-outlined m-1">
                Create!
                </button>
        </form>
    </div>;
}
export default BookCreate;
