import { useState } from "react";


function BookCreate({onCreate}){
    const [title,setTitle] = useState('');

    const handleChange = (event) =>{
       
        setTitle(event.target.value);   
       
    };
    const handleSubmit = (event)=>{
        event.preventDefault();
        onCreate(title);
        setTitle('');
    }

    return <div className="columns is-vcentered">
        <div className="column is-8">
        <h3 className="bd-notification is-primarys">Add a book</h3>
        </div>
        
        <form className="control" onSubmit={handleSubmit}>
            <label className="label">Title</label>
            <input className="input is-danger" value={title} onChange={handleChange}/>
            <button className="button is-link">Create!</button>
        </form>
    </div>;
}
export default BookCreate;
