import {useState} from "react";
import BookEdit from './BookEdit';
function BookShow({book, onDelete, onEdit}) {

    const [showEdit,
        setShowEdit] = useState(false);

    const handleDeleteClick = () => {
        onDelete(book.id);
    }

    const handleEditClick = () => {
        console.log(showEdit);
        setShowEdit(!showEdit);
    }

    const handleSubmit = (id, newTitle) => {
        setShowEdit(false);
        onEdit(id, newTitle);
    }

    let content = <h3 className="content is-small">{book.title}</h3>;
    if (showEdit) {
        content = <BookEdit onSubmit={handleSubmit} book={book}/>;
    }

    return (
        <div className="columns">
            {content}
            <div className="column">

                <button
                    className="button has-text-primary m-1 is-primary is-outlined"
                    onClick={handleEditClick}>
                    Edit
                </button>

                <button
                    className="button is-primary is-outlined m-1 has-text-primary"
                    onClick
                    ={handleDeleteClick}>
                    Delete
                </button>

            </div>
        </div>
    );
}
export default BookShow;
